import axios  from "axios";
import "./style.css"


// URL APIn
import { URL } from '../../utils/constantes/urls.js'

function Accompagnement(){

    const [Accompagnement, setAccompagnement] = useState([]);


    useEffect(()=>{
        const fetchAccompagnement = async()=>{
            try{
                const {data}= await axios.get(URL.fetchAccompagnement)
                console.log(data)
                setAccompagnement(data)
                console.log(Accompagnement)

            }catch(error){
                console.log(error.message())

            }
        }
        fetchAccompagnement()

    },[])
    return(

        <>
            <h1>Nos Accompagnements ü•ó üçü</h1>
            {Accompagnement.map((item, id)=>{
                return (
                    <React.Fragment key={id}>
                        <p className="">{item.name}</p>
                        <img className="img" src={item.image} alt=""/>
                        <p>{item.price.$numberDecimal}‚Ç¨</p>
                        <p>{item.description} </p>
                    </React.Fragment>
                )
            })}
        </>

    )

}

export default Accompagnement;
